import { useState, useEffect } from "react"

function SaveCardData(props) {
    {
        const saveCard = (cardData) => {
            props.saveCard(cardData)
        }
        {
            const [saveCard, setCardData] = useState([])

            useEffect(() => {
                fetch('http://127.0.0.1:5000/cards', {
                    'methods': 'POST',
                    //withCredentials: true,
                    //mode: 'no-cors',
                    headers: {
                        'Content-Type': 'application/json'
                    }
                })
                    .then(resp => resp.json())
                    .then(resp => setCardData(resp))
                    .catch(error => console.log(error))
            }, [])



            return (
                <section className="genCard">
                    <h2>Testing Card Image</h2>
                    {saveCard.map(cardData => {
                        return (
                            <section key={cardData.gen_id}>
                                <h2>{cardData}</h2>
                            </section>
                        )
                    })}
                </section>
            )
        }
    }
}

export default SaveCardData()
