import { useEffect, useState } from 'react';
import axios from "axios";
import { format } from "date-fns";
import './App.css';
import { Card, User } from './models.py';
import CardNameList from './components/CardNameList.js';
import Login from './components/UserLogin';
//import SaveCardData from './components/SaveCardTesting.js'
//const card = [Card]


function CardNameList2() {
  {
    const baseUrl = 'http://127.0.0.1:5000/'
    const [selectedCardName, setSelectedCardName] = useState("");
    const [selectedCardImage, setSelectedCardImage] = useState("");
    const [cardName, setCardName] = useState([])
    useEffect(() => {
      fetch(`${baseUrl}cards`, {
        'methods': 'GET',
        //withCredentials: true,
        //mode: 'no-cors',
        headers: {
          'Content-Type': 'application/json'
        }
      })
        .then(resp => resp.json())
        .then(resp => setCardName(resp))
        .catch(error => console.log(error))
    }, []);

    const handleLoadImage = () => {
      fetch(`http://127.0.0.1:5000/cards/${selectedCardName}/image`, {
        methods: "GET",
        headers: {
          "Content-Type": "image/png",
        },
      })
        .then((resp) => resp.blob())
        .then((resp) => setSelectedCardImage(URL.createObjectURL(resp)))
        .catch((error) => console.log(error));
    };

    const handleSelectChange = (event) => {
      setSelectedCardName(event.target.value);
      setSelectedCardImage("");
    };


    return (
      <section className='genCard'>
        <div className='cardList'>
          <select onChange={handleSelectChange} value={selectedCardName}>
            <option value="">--Select a card--</option>
            {cardName.map(list => {
              return (

                <option key={list.card_id} value={list.name}>
                  {list.name}
                </option>
              )


            })}
          </select>
          <button onClick={handleLoadImage} disabled={!selectedCardName}>
            Load
          </button>
        </div>
      </section >
    )
  }
}


function Navbar() {
  return (
    <div className="navbar">
      <img src="images\logo.png" alt="logo" className="navlogo" />
      <div className="header">
        <h1>Card Generator α</h1>
      </div>

      <div className="upper">
